import React, { useRef, useState } from "react";
import { Canvas, useFrame, useResource } from "react-three-fiber";
import "./styles.css";

function Box(props) {
  // This reference will give us direct access to the mesh
  const mesh = useRef();

  // Rotate mesh every frame, this is outside of React without overhead
  useFrame(() => {
    mesh.current.rotation.x += 0.01;
    mesh.current.rotation.y += 0.02;
  });

  return (
    <mesh ref={mesh} {...props}>
      <cylinderGeometry attach='geometry' args={[5, 5, 10, 8, 1]} />
      <meshPhongMaterial attach='material' color='pink' />
    </mesh>
  );
}

function Lights() {
  const [ref, light] = useResource();

  return (
    <>
      <hemisphereLight args={[0xaaaaaa, 0x000000, 0.9]} />
      <ambientLight args={[0xdc8874, 0.5]} />
      <directionalLight ref={ref} args={[0xffffff, 0.9]} position={[150, 350, 350]} />
      {light && <directionalLightHelper args={[light, 1]} />}
    </>
  );
}

export default function App() {
  // Rotate mesh every frame, this is outside of React without overhead
  const mesh = useRef();
  useFrame(() => {
    mesh.current.rotation.x += 0.01;
    mesh.current.rotation.y += 0.02;
  });
  // const [ref, light] = useResource();

  return (
    <Canvas camera={{ position: [0, 0, 50] }}>
      <Box />
      <Lights />
    </Canvas>
  );
}
